//각 전화번호 정보를 보여주는 컴포넌트
import React, { Component } from 'react'

class PhoneInfo extends Component {
    static defaultProps = {
        info : {
            name : '이름',
            phone: '010-0000-0000',
            id: 0
        }
    }
    handleRemove = () => {
        const {info, onRemove} = this.props
        onRemove(info.id)
    }
    render() {
        const style ={
            border: '1px solid black',
            padding: '8px',
            margin: '8px'
        }
        const {
            name, phone, id
        } = this.props.info
        return (
            <div style={style}>
                <div><b>{name}</b></div>
                <div>{phone}</div>
                <button onClick={this.handleRemove}>삭제</button>
            </div>
        )
    }
}

export default PhoneInfo
